import React, { Component} from 'react';
import './App.css';

class App extends Component{
  constructor(props){
    super(props);
    this.state = {
      items: [],
      isLoaded: false
    }
  }

  componentDidMount() {
    fetch('https://rickandmortyapi.com/api/character/')
    .then ( result => result.json())
    .then (json => {
      this.setState({
        items: json,
        isLoaded: true
      })
    });
  }

  render() {

    var {isLoaded, items} = this.state;

    if( !isLoaded ){
      return(
        <>Loading....</>
      );
    } else {
      return (
        <div>
          <ul>
            {items.map(item => (
              <li>{item.name.rendered}</li>
            ))}
          </ul>
        </div>
      )
    }
  }
}



export default App;
