import React, { Component} from 'react';
import './App.css';

class App extends Component{
  constructor(props){
    super(props);
    this.state = {
      character: [],
      isLoaded: false
    }
  }

  componentDidMount() {
    fetch('https://rickandmortyapi.com/api/character/ ')
    .then ( result => result.json())
    .then (json => {
      this.setState({
        character: json,
        isLoaded: true
      })
    })
  }

  render() {
    return(
      <div>
        <h1>Welcom</h1>
        {
          this.state.character ?
          this.state.character.map((results, index) =>
          <div>
            {index}---{results.name}
          </div>
          )
          :
          null
        }
      </div>
    )
  }
}



export default App;
