import React, { Component } from 'react';
import { Card, CardDeck,  CardImg, CardBody, CardTitle } from 'react-bootstrap';




export default class App extends Component {
    constructor(props) {
        super(props);
        this.state = {
            items: [],
            isLoaded: false
        }
    }
    componentDidMount() {
        fetch('https://rickandmortyapi.com/api/character/')
            .then(res => res.json())
            .then(json => {
                this.setState({
                    isLoaded: true,
                    items: json.results
                })
            });
    }
    render() {
        var { isLoaded, items } = this.state;
        if (!isLoaded) {
            return <div>Loading...</div>;
        }
        return (
            <div>



                       <div>

                         <CardDeck>
                         {items.map(item => (
                         <Card>
        <CardImg top width="100%" src={item.image} />
        <CardBody>
          <CardTitle>{item.name}</CardTitle>

        </CardBody>
      </Card>
                         ))}
                         </CardDeck>



                         </div>


            </div>
        );
    }
}
