import React from 'react';
import './App.css';

class App extends React.Component{
  constructor(){
    super();
    this.state = {
      character: []
    }
  }

  componentDidMount() {
    fetch('https://rickandmortyapi.com/api/character/ ').then((resp) => {
      resp.json().then((result) => {
        console.log(result) 
        this.setState({character:result.data}) 
      })
    })
  }

  render() {
    return(
      <div>
        <h1>Welcom</h1>
        {
          this.state.character ?
          this.state.character.map((results, i) =>
          <div>
            {i}---{results.name}
          </div>
          )
          :
          null
        }
      </div>
    )
  }
}



export default App;
