import React, { Component} from 'react';
import './App.css';

class App extends Component{
  constructor(props){
    super(props);
    this.state = {
      characters: [],
      isLoaded: false
    }
  }

  componentDidMount() {
    fetch('https://rickandmortyapi.com/api/character/ ')
    .then ( result => result.json())
    .then (json => {
      this.setState({
        characters: json,
        isLoaded: true
      })
    });
  }

  render() {

    var {isLoaded, characters} = this.state;

    if(!isLoaded){
      return(
        <div>Loading....</div>
      );
    } else {
    return (
      <div className="App">
       {characters.map(character =>(
         <p>{character.name.render}</p>
       ))}
      </div>
    )
  }
  }
}



export default App;
