import React, { Component } from 'react';
// eslint-disable-next-line no-unused-vars
import { Card, CardColumns, Row, Col, Container, ListGroup, ListGroupItem } from 'react-bootstrap';




export default class App extends Component {
    constructor(props) {
        super(props);
        this.state = {
            items: [],
            isLoaded: false
        }
    }
    componentDidMount() {
        fetch('https://rickandmortyapi.com/api/character/')
            .then(res => res.json())
            .then(json => {
                this.setState({
                    isLoaded: true,
                    items: json.results
                })
            });
    }
    render() {
        var { isLoaded, items } = this.state;
        if (!isLoaded) {
            return <div>Loading...</div>;
        }
        return (
            <div>
               <Container>
             <CardColumns>


                {items.map(item => (
                  <Card>
                  <Card.Img variant="top" src={item.image} />
                    <Card.Body>
                <Card.Title>{item.id}-{item.name}<p>{item.origin.name}</p></Card.Title>
                      <Card.Text>
                      Status:  {item.status}
                      </Card.Text>
                      <ListGroup className="list-group-flush">
                        <ListGroupItem>Cras justo odio</ListGroupItem>
                        <ListGroupItem>Dapibus ac facilisis in</ListGroupItem>
                        <ListGroupItem>Vestibulum at eros</ListGroupItem>
                      </ListGroup>
                    </Card.Body>
                  </Card>
 ))}

</CardColumns>
</Container>
                         </div>



        );
    }
}
